import "./App.css";
import Quiz from "./components/Quiz";
import React from "react";

function App() {
  const [quizObj, setQuizObj] = React.useState([]);

  React.useEffect(() => {
    fetch("https://opentdb.com/api.php?amount=5&category=18&type=multiple")
      .then((res) => res.json())
      .then((data) => setQuizObj(data.results));
  }, []);

  return (
    <div className="container">
      {JSON.stringify(quizObj)}

      <Quiz question="How would one say goodbye in Spanish?" />
      <Quiz question="In which country was the caesar salad invented?" />
      <Quiz question="Which best selling toy of 1983 caused hysteria, resulting in riots breaking in stores?" />
      <Quiz question="What is the hottest planet in our Solar System?" />
      <div className="newgame--action">
        <h4>You scored 3/5 correct answers</h4>
        <button className="btn--check--answer">Check answer</button>
        <button className="btn--check--answer">Play again!</button>
      </div>
    </div>
  );
}

export default App;
