import React from "react";

export default function Quick(props) {
  const correctAnswer = props.correct_answer;
  const Answers = props.incorrect_answers;
  // Answers.push(correctAnswer);
  const allAnswers = Answers.sort(() => 0.5 - Math.random());
  //console.log(correctAnswer);

  const newAnswers = allAnswers.map((item) => {
    return <div className="answer--selector selected">{item}</div>;
  });

  return (
    <div>
      {JSON.stringify(allAnswers)}
      <div className="each--quiz">
        <div className="quiz">
          <h3 className="question">{props.question}</h3>
          <div className="answers">{newAnswers}</div>
        </div>
      </div>
    </div>
  );
}
