import React from "react";
import he from "he";

export default function Quick(props) {
  const allAnswers = props.allAnswers.sort(() => 0.5 - Math.random());
  //console.log(correctAnswer);

  const newAnswers = allAnswers.map((item) => {
    return (
      <div className="answer--selector" data-answervalue={item}>
        {he.decode(item.text)}
      </div>
    );
  });

  return (
    <div>
      {/* {JSON.stringify(props.allAnswers)} */}
      <div className="each--quiz">
        <div className="quiz">
          <h3 className="question" data-questionid={props.id}>
            {he.decode(props.question)}
          </h3>
          <div className="answers">{newAnswers}</div>
        </div>
      </div>
    </div>
  );
}
