import React from "react";

export default function Quick(props) {
  const correctAnswer = props.incorrect_answers;
  const incorrectAnswers = props.incorrect_answers;
  const allAnswers = incorrectAnswers.push(correctAnswer);
  const shuffledAnswer = allAnswers.sort((a, b) => 0.5 - Math.random());

  const answers = shuffledAnswer.map((item) => {
    return <div className="answer--selector selected">item</div>;
  });
  return (
    <div>
      <div className="each--quiz">
        <div className="quiz">
          <h3 className="question">{props.question}</h3>
          <div className="answers">{answers}</div>
        </div>
      </div>
    </div>
  );
}
