import "./App.css";
import Quiz from "./components/Quiz";
import React from "react";
import { nanoid } from "nanoid";

function App() {
  const [quizObj, setQuizObj] = React.useState([]);

  function extendAnswerObj(answer, isCorrect) {
    return {
      id: nanoid(),
      text: answer,
      selected: false,
      isCorrect: isCorrect,
    };
  }

  function dataRestructor(data) {
    const questionsObj = data.map((result) => ({
      ...result,
      correct_answer: extendAnswerObj(result.correct_answer, true),
      incorrect_answers: result.incorrect_answers.map((prevObj) =>
        extendAnswerObj(prevObj, false)
      ),
    }));
    return questionsObj;
  }
  React.useEffect(() => {
    fetch("https://opentdb.com/api.php?amount=5&category=18")
      .then((res) => res.json())
      .then((data) => setQuizObj(dataRestructor(data.results)));
  }, []);

  function answerSelector(questionid, answerid) {
    // /setQuizObj.filter(item.id == questionid);
    // console.log(questionid, answerid);
    setQuizObj((prevData) =>
      prevData.map((eachQuestion) => {
        if (
          (eachQuestion.id === questionid &&
            eachQuestion.correct_answer.id === answerid) ||
          eachQuestion.incorrect_answers.id === answerid
        ) {
          return { ...eachQuestion };
        } else {
          return "unknown error";
        }
      })
    );
  }

  const allQuize = quizObj.map((item) => {
    const answers = [...item.incorrect_answers, item.correct_answer];
    const randomAnswers = answers.sort(() => 0.5 - Math.random());

    return (
      <Quiz
        key={nanoid()}
        id={nanoid()}
        question={item.question}
        correct_answer={item.correct_answer.id}
        incorrect_answers={item.incorrect_answers}
        allAnswers={randomAnswers}
        handleAnswerSelector={answerSelector}
      />
    );
  });

  //document.getElementsByClassName("answer--selector").
  return (
    <div className="container">
      {/* {JSON.stringify(quizObj)} */}

      {allQuize}
      <div className="newgame--action">
        <h4>You scored 3/5 correct answers</h4>
        <button className="btn--check--answer">Check answer</button>
        <button className="btn--check--answer">Play again!</button>
      </div>
    </div>
  );
}

export default App;
