import "./App.css";
import Quiz from "./components/Quiz";
import React from "react";
import { nanoid } from "nanoid";

function App() {
  const [quizObj, setQuizObj] = React.useState([]);

  React.useEffect(() => {
    fetch("https://opentdb.com/api.php?amount=1&category=18&type=multiple")
      .then((res) => res.json())
      .then((data) => {
        data.results.map((result, index) => {
          [
            ...result,
            result.incorrect_answers.map((answer) => {
              return extendAnswerObj(answer, false);
            }),
          ];
        });
      });
  }, []);

  // function extendAnswerObj(answer, isCorrect) {
  //   return {
  //     id: uuidv4(),
  //     text: answer,
  //     selected: false,
  //     isCorrect: isCorrect,
  //   };
  // }

  const allQuize = quizObj.map((item) => {
    // const correctAnswer = item.correct_answer;
    const incorrectAnswers = item.incorrect_answers.map((prevObj) =>
      extendAnswerObj(prevObj, false)
    );

    const answers = [...incorrectAnswers, extendAnswerObj(item.correct_answer)];
    //answers.push(correctAnswer);

    //JSON.stringify(quizObj+ <br/>);

    return (
      <Quiz
        key={nanoid()}
        id={nanoid()}
        question={item.question}
        correct_answer={item.correct_answer}
        incorrect_answers={item.incorrect_answers}
        allAnswers={answers}
      />
    );
  });

  //document.getElementsByClassName("answer--selector").
  return (
    <div className="container">
      {JSON.stringify(quizObj)}

      {allQuize}
      <div className="newgame--action">
        <h4>You scored 3/5 correct answers</h4>
        <button className="btn--check--answer">Check answer</button>
        <button className="btn--check--answer">Play again!</button>
      </div>
    </div>
  );
}

export default App;
